    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}S'inscrire {{ parent() }}{% endblock %}

{% block main %}

    <div id=\"registerForm\">

        {{ form_start(registrationForm) }}
        <div id=\"formCreaCompte\">
            {% for flashError in app.flashes('verify_email_error') %}
                <div class=\"alert alert-danger\" role=\"alert\">{{ flashError }}</div>
            {% endfor %}

            <h2>S'inscrire</h2>
                {{ form_row(registrationForm.email) }}
                {{ form_row(registrationForm.username, {
                    label: 'Pseudo'
                })}}
                {{ form_row(registrationForm.plainPassword, {
                    label: 'Mot de passe'
                }) }}

                <button type=\"submit\" id=\"bouton-register\">Créer mon compte</button>


        </div>
        {{ form_end(registrationForm) }}

        <div id=\"se-login\">
            <h4>Tu as déjà un compte ? <span>Connecte-toi</span></h4>
            <p><a href=\"{{ path('app_login')}}\">Se connecter</a></p>
        </div>

    </div>
{% endblock %}
", "registration/register.html.twig", "C:\\wamp64\\www\\PROJET-PERSO\\date-peremption\\templates\\registration\\register.html.twig");
